INSERT OR REPLACE INTO walking_map_features (feature_id, name, description, layer_id, geometry_type, geometry_wkt, meta_data) VALUES
('20251229_baileng_intake', '白冷圳進水口', '位於天輪發電廠對岸，大甲溪與東卯溪匯流處。這裡是白冷圳的取水源頭，透過地下管線引穿大甲溪底，展現了人定勝天的水利智慧。', 2, 'Point', 'POINT(121.0155 24.2100)', '{"markdown_file": "20251229_baileng_intake.md", "tags": ["智慧水圳"]}'),
('20251229_baileng_malipu', '白冷圳馬力埔支線', '位於新社馬力埔社區，沿線有著名的彩繪小徑。這條支線不僅灌溉了新社的花海與香菇，更與當地人文景觀深度結合。', 2, 'Point', 'POINT(120.8250 24.2250)', '{"markdown_file": "20251229_baileng_malipu.md", "tags": ["智慧水圳"]}'),
('20251229_houli_low_head_power', '后里圳低落差示範電廠', '全台首座貫流式水輪發電機組，利用后里圳僅 3.6 公尺的落差進行發電。證明了小水力發電在平原灌溉渠道中的巨大潛力。', 2, 'Point', 'POINT(120.7350 24.2980)', '{"markdown_file": "20251229_houli_low_head_power.md", "tags": ["智慧水圳"]}'),
('20251229_zhonghe_gate', '忠和中排水截水閘門', '位於龍井區，具備自動化水位監測與控制功能。在防洪與區域排水管理中扮演關鍵角色，是現代化智慧水利的實踐。', 2, 'Point', 'POINT(120.5300 24.2250)', '{"markdown_file": "20251229_zhonghe_gate.md", "tags": ["智慧水圳"]}'),
('20251229_huyan_fuxing', '虎眼一圳福興支線', '流經神岡/大雅交界，結合福興生態園區。這裡展現了水利工程與生態保育共存的理念，是觀察水域生態的好去處。', 2, 'Point', 'POINT(120.6699 24.2764)', '{"markdown_file": "20251229_huyan_fuxing.md", "tags": ["智慧水圳"]}'),
('20251229_yuanlin_branch', '員林支線', '位於大雅區神林路旁，穿越繁忙的半都會區。見證了農業灌溉渠道如何適應都市化發展，繼續滋潤這片土地。', 2, 'Point', 'POINT(120.6579 24.2459)', '{"markdown_file": "20251229_yuanlin_branch.md", "tags": ["智慧水圳"]}'),
('20251229_taliangou_branch', '塔蓮溝支線', '大雅區重要的灌排兼用渠道，近年進行了護岸整治工程，提升了排水效率與安全性。', 2, 'Point', 'POINT(120.6500 24.2400)', '{"markdown_file": "20251229_taliangou_branch.md", "tags": ["智慧水圳"]}'),
('20251229_huludun_intake', '葫蘆墩圳進水口', '位於豐原萬順二街盡頭，是大甲溪水進入台中盆地的第一站。這裡保存了歷史遺跡，也設有現代化的監測設備。', 2, 'Point', 'POINT(120.7448 24.2790)', '{"markdown_file": "20251229_huludun_intake.md", "tags": ["智慧水圳"]}');
